    val listData: ArrayList<Chars>
        get() {
            val list = arrayListOf<Chars>()
            for (position in charFullNames.indices){
                val char = Chars()
                char.full_name = charFullNames[position]
                char.biodata_gender = gender[position]
                char.biodata_species = species[position]
                char.biodata_status = status[position]
                char.biodata_short = shortBiodata[position]
                char.photo = charPhotos[position]
                list.add(char)
            }
            return list
        }
